controller.left.onEvent(ControllerButtonEvent.Pressed, function () {
    dir = 1
    if (play == 0) {
        if (x != 0) {
            x += -1
        }
    }
})
info.onScore(100, function () {
    info.changeLifeBy(1)
    info.setScore(0)
})
controller.right.onEvent(ControllerButtonEvent.Pressed, function () {
    dir = 0
    if (play == 0) {
        if (x != 49) {
            x += 1
        }
    }
})
controller.down.onEvent(ControllerButtonEvent.Pressed, function () {
    if (play == 0) {
        if (y != 15) {
            y += 1
        }
    }
})
scene.onOverlapTile(SpriteKind.Player, assets.tile`myTile2`, function (sprite, location) {
    mySprite.setPosition(20, 20)
    info.changeLifeBy(-1)
})
controller.menu.onEvent(ControllerButtonEvent.Pressed, function () {
    if (play == 0) {
        play = 1
        ran = 0
    } else if (play == 1) {
        play = 0
        ran2 = 0
    }
})
info.onLifeZero(function () {
    if (play == 1) {
        play = 0
        ran2 = 0
        effects.bubbles.startScreenEffect(1000)
    } else {
    	
    }
})
blockMenu.onMenuOptionSelected(function (option, index) {
    if (blockMenu.isMenuOpen()) {
        if (option == "Mario") {
            game.setDialogTextColor(2)
            game.showLongText("It me, Mario", DialogLayout.Bottom)
            playerKind = 0
            blockMenu.closeMenu()
            play = 0
        } else if (option == "Luigi") {
            game.setDialogTextColor(7)
            game.showLongText("Luigi Time!", DialogLayout.Bottom)
            playerKind = 1
            blockMenu.closeMenu()
            play = 0
        } else if (option == "Red toad") {
            game.setDialogTextColor(2)
            game.showLongText("Oh yeah lets go", DialogLayout.Bottom)
            playerKind = 2
            blockMenu.closeMenu()
            play = 0
        } else if (option == "Blue toad") {
            game.setDialogTextColor(8)
            game.showLongText("Oh yeah lets go", DialogLayout.Bottom)
            playerKind = 3
            blockMenu.closeMenu()
            play = 0
        } else if (option == "Day") {
            game.setDialogTextColor(1)
            game.splash("color set")
            scene.setBackgroundColor(9)
            blockMenu.closeMenu()
            play = 0
        } else if (option == "Night") {
            game.setDialogTextColor(1)
            game.splash("color set")
            scene.setBackgroundColor(15)
            blockMenu.closeMenu()
            play = 0
        } else if (option == "Red") {
            game.setDialogTextColor(1)
            game.splash("color set")
            scene.setBackgroundColor(2)
            blockMenu.closeMenu()
            play = 0
        } else if (option == "Blue") {
            game.setDialogTextColor(1)
            game.splash("color set")
            scene.setBackgroundColor(8)
            blockMenu.closeMenu()
            play = 0
        }
    }
})
